using System.Collections; using System.Collections.Generic; using UnityEngine;  public class Farmer : MonoBehaviour {      public float speed = 10;     public Transform carryLocation; // this is empty gameobject as a child of player, object will be carried on this position     Transform currentfarmer = null;      void Update()     {         // basic movement         var x = Input.GetAxis("Horizontal") * speed * Time.deltaTime;         var y = Input.GetAxis("Vertical") * speed * Time.deltaTime;         transform.Translate(x, y, 0);          // press drop to drop item, if carrying something         if (Input.GetButtonDown("Drop"))         {             if (currentfarmer != null)             {                 // remove as child                 currentfarmer.parent = null;                  //set position near player                 currentfarmer.position = transform.GetComponent<SpriteRenderer>().bounds.max;                  // release reference                 currentfarmer = null;             }         }     }      void OnTriggerEnter(Collider collider)     {         // when player collide with the farmer         if (collider.gameObject.tag=="Player")         {             // take reference to that collided object             currentfarmer = collider.transform;              // move it to carrying point             currentfarmer.position = carryLocation.position;              // make it as a child of player, so it moves along with player             currentfarmer.parent = transform;         }     } }